package pl.com.bottega.cineman.model.commands;import java.math.BigDecimal;import java.util.Map;public class DefineMoviePricesCommand implements Validatable {	private Map<String, BigDecimal> prices;	private Long movieId;	public DefineMoviePricesCommand() {	}	public Map<String, BigDecimal> getPrices() {		return prices;	}	public void setPrices(Map<String, BigDecimal> prices) {		this.prices = prices;	}	public void setMovieId(Long movieId) {		this.movieId = movieId;	}	public Long getMovieId() {		return movieId;	}	@Override	public void validate(ValidationErrors errors) {		if (!prices.containsKey("regular"))			errors.add("kind", "regular price is required");		if (!prices.containsKey("student"))			errors.add("kind", "student price is required");		for (Map.Entry<String, BigDecimal> pair : prices.entrySet()) {			if (pair.getValue().compareTo(BigDecimal.ZERO) < 0)				errors.add("price", "cannot be lower than zero");		}	}}